-- ----------------------------
-- Table structure for t_wechat_news
-- ----------------------------
DROP TABLE IF EXISTS `t_wechat_news`;
CREATE TABLE `t_wechat_news` (
  `media_id` varchar(64) NOT NULL,
  `parent_media_id` varchar(64) DEFAULT NULL,
  `news_title` varchar(255) DEFAULT NULL,
  `news_author` varchar(255) DEFAULT NULL,
  `news_digest` varchar(512) DEFAULT NULL,
  `content` text,
  `create_time` datetime DEFAULT NULL,
  `update_time` datetime DEFAULT NULL,
  `thumb_media_id` varchar(64) DEFAULT NULL,
  `content_source_url` varchar(512) DEFAULT NULL,
  `show_cover_pic` int(255) DEFAULT NULL,
  `news_url` varchar(255) DEFAULT NULL,
  `thumb_media_url` varchar(512) DEFAULT NULL,
  `need_open_comment` int(255) DEFAULT NULL,
  `only_fans_can_comment` int(255) DEFAULT NULL,
  `thumb_media_local_url` varchar(512) DEFAULT NULL,
  PRIMARY KEY (`media_id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

-- ----------------------------
-- Records of t_wechat_news
-- ----------------------------
BEGIN;
INSERT INTO `t_wechat_news` VALUES ('JDfSfJl9ZkaORw9dFDGdcARzggEudtllZYdcoP5u1EE', NULL, '对不起，我离不开手机', '同小恒', '手机，似乎已经成为我们身体的一部分了', '<section data-role=\"outer\" label=\"Powered by 135editor.com\" style=\"font-size: 16px;white-space: normal;font-family: 微软雅黑;\"><section data-role=\"paragraph\" class=\"_135editor\" style=\"border-width: 0px;border-style: none;border-color: initial;box-sizing: border-box;\"><p style=\"margin-right: 15px;margin-left: 15px;font-family: Helvetica, Arial, sans-serif;font-size: medium;line-height: 1.75em;\"><span style=\"font-size: 15px;\">或许说手机可能还有点不全面</span></p><p style=\"margin-right: 15px;margin-left: 15px;font-family: Helvetica, Arial, sans-serif;font-size: medium;line-height: 1.75em;\"><span style=\"font-size: 15px;\">毕竟平板，电脑也是烂大街的产品了</span></p><p style=\"margin-right: 15px;margin-left: 15px;font-family: Helvetica, Arial, sans-serif;font-size: medium;line-height: 1.75em;\"><span style=\"font-size: 15px;\">现在大多数人生活优越，即使拮据也少不了这些智能产品</span></p><p style=\"margin-right: 15px;margin-left: 15px;line-height: 1.75em;\"><span style=\"font-size: 15px;\">每天起床看第一眼的是手机</span></p><p style=\"margin-right: 15px;margin-left: 15px;line-height: 1.75em;\"><span style=\"font-size: 15px;\">每天晚上睡觉前最后一眼也是手机</span></p><p style=\"margin-right: 15px;margin-left: 15px;line-height: 1.75em;\"><span style=\"font-size: 15px;\">这巴掌大的小东西已贯穿生活的一点一滴</span></p><p style=\"margin-right: 15px;margin-left: 15px;line-height: 1.75em;\"><span style=\"font-size: 15px;\">不知什么时候我们已经离不开它了</span></p><p style=\"margin-right: 15px;margin-left: 15px;line-height: 1.75em;\"><span style=\"font-size: 15px;\">似乎已于我们融为一体</span></p><p style=\"margin-right: 16px;margin-left: 16px;text-align: center;\"><img class=\"\" data-copyright=\"0\" data-cropselx1=\"0\" data-cropselx2=\"182\" data-cropsely1=\"0\" data-cropsely2=\"181\" data-ratio=\"0.9970414201183432\" data-s=\"300,640\" data-src=\"https://mmbiz.qpic.cn/mmbiz_jpg/Qz1Dy9HLTM7O3hlA0vsmZNLhvRHKUHjBaOzCQB3RrIsac5EvAXrePGEbHKTnjxbiaNmrmJ1z8UrFGCibeF1wAk4w/640?wx_fmt=jpeg\" data-type=\"jpeg\" data-w=\"338\" style=\"height: 181px;width: 182px;\"  /></p><p style=\"margin-right: 15px;margin-left: 15px;line-height: 1.75em;\"><span style=\"font-size: 15px;\">它是我们无聊时的玩伴</span></p><p style=\"margin-right: 15px;margin-left: 15px;line-height: 1.75em;\"><span style=\"font-size: 15px;\">它是我们无助时的稻草</span></p><p style=\"margin-right: 15px;margin-left: 15px;line-height: 1.75em;\"><span style=\"font-size: 15px;\">它是我们空虚时的寄托</span></p><p style=\"margin-right: 15px;margin-left: 15px;line-height: 1.75em;\"><span style=\"font-size: 15px;\">它仿佛一个影子，形影不离</span></p><p><br  /></p><p style=\"margin-right: 15px;margin-left: 15px;line-height: 1.75em;\"><span style=\"font-size: 15px;\">你肯定感受过找不到它时的慌张</span></p><p style=\"margin-right: 15px;margin-left: 15px;line-height: 1.75em;\"><span style=\"font-size: 15px;\">也曾与它彻夜面谈</span></p><p style=\"margin-right: 15px;margin-left: 15px;line-height: 1.75em;\"><span style=\"font-size: 15px;\">惺惺相惜，怕它没电，怕遗忘它</span></p><p style=\"margin-right: 15px;margin-left: 15px;line-height: 1.75em;\"><span style=\"font-size: 15px;\">日常相伴却总爱不释手<br  /></span></p><p style=\"margin-right: 15px;margin-left: 15px;line-height: 1.75em;\"><span style=\"font-size: 15px;\">沙发，藤椅，公交，地铁，甚至上厕所都不忍分开</span></p><p style=\"margin-right: 15px;margin-left: 15px;line-height: 1.75em;\"><span style=\"font-size: 15px;\">坐着，站着，躺着无论什么姿势也无碍缠绵</span></p><p style=\"margin: 10px 8px;text-align: center;line-height: 1.75em;\"><img class=\"\" data-copyright=\"0\" data-cropselx1=\"0\" data-cropselx2=\"330\" data-cropsely1=\"0\" data-cropsely2=\"248\" data-ratio=\"0.75\" data-s=\"300,640\" data-src=\"https://mmbiz.qpic.cn/mmbiz_jpg/Qz1Dy9HLTM7O3hlA0vsmZNLhvRHKUHjB42WN1vnXMITicWV0hDicOIC76gWJqicNevz6zAQIXUtNfISLx2EY2VVVg/640?wx_fmt=jpeg\" data-type=\"jpeg\" data-w=\"600\" style=\"height: 248px;width: 331px;\"  /></p><p style=\"margin-right: 15px;margin-left: 15px;line-height: 1.75em;\"><span style=\"font-size: 15px;\">或许，它带给我们太多太多</span></p><p style=\"margin-right: 15px;margin-left: 15px;line-height: 1.75em;\"><span style=\"font-size: 15px;\">感谢它带来时效多彩的讯息</span></p><p style=\"margin-right: 15px;margin-left: 15px;line-height: 1.75em;\"><span style=\"font-size: 15px;\">感叹它出行时无所不知的能力</span></p><p style=\"margin-right: 15px;margin-left: 15px;line-height: 1.75em;\"><span style=\"font-size: 15px;\">庆幸它缓解人群中孤身一人的尴尬</span></p><p style=\"margin: 10px 8px;text-align: center;line-height: 1.75em;\"><img class=\"\" data-copyright=\"0\" data-cropselx1=\"0\" data-cropselx2=\"542\" data-cropsely1=\"0\" data-cropsely2=\"332\" data-ratio=\"0.612\" data-s=\"300,640\" data-src=\"https://mmbiz.qpic.cn/mmbiz_jpg/Qz1Dy9HLTM7O3hlA0vsmZNLhvRHKUHjBO3zBXlBQGHst1fN1IzSIGDNqfT6wXRKxfYDAhavJJoQ5dv22D1ed7A/640?wx_fmt=jpeg\" data-type=\"jpeg\" data-w=\"500\" style=\"height: 232px;width: 379px;\"  /></p><p style=\"margin-right: 15px;margin-left: 15px;line-height: 1.75em;\"><span style=\"font-size: 15px;\">但是，事物总有两面性</span></p><p style=\"margin-right: 15px;margin-left: 15px;line-height: 1.75em;\"><span style=\"font-size: 15px;\">与它相伴的日子眼里只有它</span></p><p style=\"margin-right: 15px;margin-left: 15px;line-height: 1.75em;\"><span style=\"font-size: 15px;\">它独得恩宠&nbsp; 我们忘却雨露均沾</span></p><p style=\"margin-right: 15px;margin-left: 15px;line-height: 1.75em;\"><span style=\"font-size: 15px;\">相比它带来的资讯，趣味和信息交流</span></p><p style=\"margin-right: 15px;margin-left: 15px;line-height: 1.75em;\"><span style=\"font-size: 15px;\">它引发的近视，寡谈，肢体劳损可以忽略不计吗？</span></p><p><br  /></p><p style=\"margin-right: 15px;margin-left: 15px;line-height: 1.75em;\"><span style=\"font-size: 15px;\">与之相伴的时间总是过得飞快</span></p><p style=\"margin-right: 15px;margin-left: 15px;line-height: 1.75em;\"><span style=\"font-size: 15px;\">往沙发一躺几个小时就过去了</span></p><p style=\"margin-right: 15px;margin-left: 15px;line-height: 1.75em;\"><span style=\"font-size: 15px;\">惰性也由此加深<br  /></span></p><p style=\"margin-right: 15px;margin-left: 15px;line-height: 1.75em;\"><span style=\"font-size: 15px;\">无论思想还是行动都显得些许疲惫</span></p><p style=\"margin-right: 15px;margin-left: 15px;line-height: 1.75em;\"><span style=\"text-align: center;font-size: 15px;\">你能想起被手机支配的恐惧吗？</span></p><p style=\"margin: 10px 8px;line-height: 1.75em;text-align: center;\"><img class=\"\" data-copyright=\"0\" data-ratio=\"0.8521303258145363\" data-s=\"300,640\" data-src=\"https://mmbiz.qpic.cn/mmbiz_jpg/Qz1Dy9HLTM7O3hlA0vsmZNLhvRHKUHjBicKvLp5kfUI4lxJqiaCx9QqTNEXKG2DDbHkicpOm8Vjc8Pgw0ZFCVw3cQ/640?wx_fmt=jpeg\" data-type=\"jpeg\" data-w=\"399\" style=\"height: 313px;width: 367px;\"  /></p><p style=\"margin-right: 15px;margin-left: 15px;line-height: 1.75em;\"><span style=\"font-size: 15px;\">那么接下来我是不是该劝大家不要玩手机了？</span></p><p style=\"margin-right: 15px;margin-left: 15px;line-height: 1.75em;\"><span style=\"font-size: 15px;\">但我不会这样做</span></p><p style=\"margin-right: 15px;margin-left: 15px;line-height: 1.75em;\"><span style=\"font-size: 15px;\">因为我知道这是不可能的事</span></p><p style=\"margin-right: 15px;margin-left: 15px;line-height: 1.75em;\"><span style=\"font-size: 15px;\">从手机的开始普及到今时今日</span></p><p style=\"margin-right: 15px;margin-left: 15px;line-height: 1.75em;\"><span style=\"font-size: 15px;\">我们自己会清楚这历年生活的变化</span></p><p style=\"margin-right: 15px;margin-left: 15px;line-height: 1.75em;\"><span style=\"font-size: 15px;\">得到什么&nbsp; 失去什么</span></p><p style=\"margin-right: 15px;margin-left: 15px;line-height: 1.75em;\"><span style=\"font-size: 15px;\">太多太多新闻已指出</span></p><p style=\"margin-right: 15px;margin-left: 15px;line-height: 1.75em;\"><span style=\"font-size: 15px;\">“低头族”这个词语早已耳熟能详</span></p><p style=\"margin-right: 15px;margin-left: 15px;line-height: 1.75em;\"><span style=\"font-size: 15px;\">我们清楚个中厉害，却疲于改正</span></p><p style=\"margin: 10px 8px;text-align: center;line-height: 1.75em;\"><img class=\"\" data-copyright=\"0\" data-cropselx1=\"1\" data-cropselx2=\"414\" data-cropsely1=\"0\" data-cropsely2=\"205\" data-ratio=\"0.4957081545064378\" data-s=\"300,640\" data-src=\"https://mmbiz.qpic.cn/mmbiz_jpg/Qz1Dy9HLTM7O3hlA0vsmZNLhvRHKUHjB4ShConyYvqGUTxicib3lUUPTownX3OhvnA0QB1dEo4Ac7erITQW9IWDA/640?wx_fmt=jpeg\" data-type=\"jpeg\" data-w=\"466\" style=\"height: 196px;width: 396px;\"  /></p><p style=\"margin-right: 15px;margin-left: 15px;line-height: 1.75em;\"><span style=\"font-size: 15px;\">在羡慕完美曲线只能出现在别人身上时</span></p><p style=\"margin-right: 15px;margin-left: 15px;line-height: 1.75em;\"><span style=\"font-size: 15px;\">在聚会时，别人侃侃而谈落落大方，而你的学识却无从融入话题时</span></p><p style=\"margin-right: 15px;margin-left: 15px;line-height: 1.75em;\"><span style=\"font-size: 15px;\">在闲暇时间找不到一个好友游玩谈心时</span></p><p style=\"margin-right: 15px;margin-left: 15px;line-height: 1.75em;\"><span style=\"font-size: 15px;\">在躲在屏幕后面看着朋友圈哪哪多漂亮时</span></p><p style=\"margin-right: 15px;margin-left: 15px;line-height: 1.75em;\"><span style=\"font-size: 15px;\">是否感觉到我们的生活似乎缺少了什么</span></p><p style=\"margin: 10px 8px;text-align: center;\"><img class=\"\" data-copyright=\"0\" data-ratio=\"0.6283333333333333\" data-s=\"300,640\" data-src=\"https://mmbiz.qpic.cn/mmbiz_jpg/Qz1Dy9HLTM7O3hlA0vsmZNLhvRHKUHjBQQibml1uDa9rXL0ChtDXTXGMlYvhSYVAKZlC5cXAibGVQw4ibaasLqOFQ/640?wx_fmt=jpeg\" data-type=\"jpeg\" data-w=\"600\" style=\"height: 244px;width: 389px;\"  /></p><p style=\"margin-right: 15px;margin-left: 15px;line-height: 1.75em;\"><span style=\"font-size: 15px;\">离开手机在今天基本是与世隔绝<br  /></span></p><p style=\"margin-right: 15px;margin-left: 15px;line-height: 1.75em;\"><span style=\"font-size: 15px;\">所以完全舍弃并不现实</span></p><p style=\"margin-right: 15px;margin-left: 15px;line-height: 1.75em;\"><span style=\"font-size: 15px;\">但如果你可以抽出2小时浪费在手机上的时间<br  /></span></p><p style=\"margin-right: 15px;margin-left: 15px;line-height: 1.75em;\"><span style=\"font-size: 15px;\">去运动，你将会有个健美的身材</span></p><p style=\"margin-right: 15px;margin-left: 15px;line-height: 1.75em;\"><span style=\"font-size: 15px;\">去阅读，你将会有博古通今的才气</span></p><p style=\"margin-right: 15px;margin-left: 15px;line-height: 1.75em;\"><span style=\"font-size: 15px;\">去交友，你将拥有广泛多彩的朋友圈</span></p><p style=\"margin-right: 15px;margin-left: 15px;line-height: 1.75em;\"><span style=\"font-size: 15px;\">攒点时间去旅行，你才会发现，原来屏幕后面的美景远不如自己亲身体验来得惊艳</span></p><p><br  /></p><p style=\"margin-right: 15px;margin-left: 15px;line-height: 1.75em;\"><span style=\"font-size: 15px;\">取舍自在人心</span></p><p style=\"margin-right: 15px;margin-left: 15px;line-height: 1.75em;\"><span style=\"font-size: 15px;\">每个人的追求都不一样<br  /></span></p><p style=\"margin-right: 15px;margin-left: 15px;line-height: 1.75em;\"><span style=\"font-size: 15px;\">你可以选择忽视，继续原来的慵懒</span></p><p style=\"margin-right: 15px;margin-left: 15px;line-height: 1.75em;\"><span style=\"font-size: 15px;\">你也可以做出改变，去追求本该属于你的理想生活</span></p><p style=\"margin-right: 15px;margin-left: 15px;line-height: 1.75em;\"><span style=\"font-size: 15px;\">我尊重你的选择。路，是自己在走。</span></p><p><br  /></p><section data-role=\"outer\" label=\"Powered by 135editor.com\"><section class=\"_135editor\" data-tools=\"135编辑器\" data-id=\"702\" style=\"border-width: 0px;border-style: none;border-color: initial;box-sizing: border-box;\"><section style=\"font-size: 16.3636px;text-align: center;word-wrap: break-word !important;box-sizing: border-box !important;\"><section style=\"max-width: 100%;display: inline-block;word-wrap: break-word !important;box-sizing: border-box !important;\"><section style=\"max-width: 100%;display: inline-block;word-wrap: break-word !important;box-sizing: border-box !important;\"><p style=\"max-width: 100%;word-wrap: normal;min-height: 1em;white-space: pre-wrap;box-sizing: border-box !important;\"><strong style=\"max-width: 100%;box-sizing: border-box !important;word-wrap: break-word !important;\">·END·</strong><br style=\"max-width: 100%;word-wrap: break-word !important;box-sizing: border-box !important;\"  />&nbsp;</p></section><section class=\"135title\" style=\"margin: 0em 0.5em 0.1em;max-width: 100%;color: rgb(117, 102, 62);font-size: 1.8em;line-height: 1;font-weight: inherit;word-wrap: break-word !important;box-sizing: border-box !important;\"><span style=\"font-size: 20px;\"><strong class=\"135brush\" data-brushtype=\"text\" style=\"padding-right: 50px;padding-bottom: 3px;padding-left: 50px;border-bottom: 1px solid rgb(2, 2, 2);border-top-color: rgb(2, 2, 2);border-right-color: rgb(2, 2, 2);border-left-color: rgb(2, 2, 2);display: block;line-height: 28px;max-width: 100%;box-sizing: border-box !important;word-wrap: break-word !important;\">你喜欢的</strong></span></section><section class=\"135brush\" data-brushtype=\"text\" style=\"margin: 0.5em 1em;max-width: 100%;font-size: 1em;line-height: 1;font-weight: inherit;text-align: inherit;text-decoration: inherit;color: rgb(117, 102, 62);word-wrap: break-word !important;box-sizing: border-box !important;\"><p>我都有</p><section style=\"width: 200px;\"><p><img class=\"\" data-copyright=\"0\" data-cropselx1=\"0\" data-cropselx2=\"200\" data-cropsely1=\"0\" data-cropsely2=\"200\" data-ratio=\"1\" data-src=\"https://mmbiz.qpic.cn/mmbiz_jpg/w9GMnwKCInXNOichjCSFf5jFWwAgJbNRZNMNtcVlCmyh1R0CFwMUwj0ibcgtgX1bArry3OmwXGvUA4tcd3MI27Iw/640?wx_fmt=jpeg\" data-type=\"jpeg\" data-w=\"860\" data-width=\"100%\" style=\"height: 200px;width: 200px;\"  /></p></section></section><span style=\"padding: 0.3em;border-radius: 0.3em;box-shadow: rgb(102, 102, 102) 0.1em 0.1em 0.1em;color: rgb(255, 255, 255);display: inline-block;font-size: 1em;font-weight: inherit;max-width: 100%;text-align: inherit;text-decoration: inherit;background-color: rgb(117, 102, 62);box-sizing: border-box !important;word-wrap: break-word !important;\">你好，同恒科技</span></section></section></section><section data-role=\"paragraph\" class=\"_135editor\" style=\"border-width: 0px;border-style: none;border-color: initial;box-sizing: border-box;\"><p><br  /></p></section></section><p><br  /></p></section></section><p><br  /></p>', '2018-08-16 11:01:50', '2018-08-16 15:41:02', 'JDfSfJl9ZkaORw9dFDGdcFTdssGV1nuDyWi34Sl8QBI', '', 0, 'http://mp.weixin.qq.com/s?__biz=MzUxMzI4MTIzMA==&mid=100000081&idx=1&sn=fd6eea09266826a8b3020f4c47b94fc1&chksm=7956dcf04e2155e6d27bf5906cb069dd860408f80233cda56c5117f09a77321e239d1ddd32c2#rd', 'http://mmbiz.qpic.cn/mmbiz_jpg/w9GMnwKCInVTQjxYSXN8czH812zfKbIQHVyZgd021Oe4cria3I6E2EdsZoPNqIZ1e6w1ypNPmKzVANC7h298bng/0?wx_fmt=jpeg', 1, 0, '/upload/2018-08-16/79de61ea0b444c379cfabd5e01359140.jpeg');
INSERT INTO `t_wechat_news` VALUES ('JDfSfJl9ZkaORw9dFDGdcCI6xrwBz4RWOfRCEe45Ovc', NULL, '毕业季  非失业季！', '饭小稀', '自信、自知、自立 以梦为马，未来可期', '<section data-role=\"outer\" label=\"Powered by 135editor.com\" style=\"font-size:16px;\"><p style=\"text-align: center;line-height: 2em;\">又是一个毕业季</p><p style=\"text-align: center;line-height: 2em;\">全国800万毕业生</p><p style=\"text-align: center;line-height: 2em;\">摩肩接踵的人才市场</p><p style=\"text-align: center;line-height: 2em;\">搭上行囊的跨出校园</p><p style=\"text-align: center;line-height: 2em;\">迷茫、困惑</p><p style=\"text-align: center;line-height: 2em;\">哪里才是正确方向？</p><p><br  /></p><p style=\"text-align: center;line-height: 2em;\">追剧打机睡懒觉的日子</p><p style=\"text-align: center;line-height: 2em;\">不再心安理得</p><p style=\"text-align: center;line-height: 2em;\">生活，容不得碌碌无为的人<br  /></p><p style=\"text-align: center;line-height: 2em;\">你不得不成长</p><p style=\"text-align: center;line-height: 2em;\"><br  /></p><p style=\"text-align: center;\"><img class=\"\" data-ratio=\"1.0421052631578946\" data-src=\"https://mmbiz.qpic.cn/mmbiz_jpg/w9GMnwKCInU9PticNZeHeIb2tHC5XibpaO4fPIeWCDsTO5x6CjIbB6NAgXHqjXooURdAiaziaTFv1iaIiazXpltBeEKw/640?wx_fmt=jpeg\" data-type=\"jpeg\" data-w=\"380\" style=\"width: 176px;height: 183px;\"  /></p><p style=\"text-align: center;\"><br  /></p><p style=\"text-align: center;line-height: 2em;\">路漫漫其修远兮</p><p style=\"text-align: center;line-height: 2em;\">汝将规划而求索</p><p style=\"text-align: center;line-height: 2em;\">人生需要规划</p><p style=\"text-align: center;line-height: 2em;\">无论近日或是远景</p><p><br  /></p><p style=\"text-align: center;line-height: 2em;\">如果你对生活感到迷茫</p><p style=\"text-align: center;line-height: 2em;\">请给自己点时间</p><p style=\"text-align: center;line-height: 2em;\">安静想想</p><p style=\"text-align: center;line-height: 2em;\">我，未来该怎么做？</p><p><br  /></p><section class=\"_135editor\" data-tools=\"135编辑器\" data-id=\"71747\" style=\"border-width: 0px;border-style: none;border-color: initial;box-sizing: border-box;\"><section class=\"135bg\" style=\"padding: 20px;margin-right: auto;margin-left: auto;background-image: url(&quot;https://mmbiz.qpic.cn/mmbiz_jpg/w9GMnwKCInU9PticNZeHeIb2tHC5XibpaOc2dLAW5IxHsf0xCFyyQpxZWoWIl0ybSt3fUX30OMQtsYaQ9CPna3zw/640?wx_fmt=jpeg&quot;);background-size: cover;background-position: 0% 50%;background-repeat: no-repeat;box-sizing: border-box;\"><section style=\"padding: 5px;box-sizing: border-box;\" class=\"135brush\" data-style=\"text-align: justify; line-height: 2em;color:#A5A5A5;\"><p style=\"text-align: justify;line-height: 2em;\">我能否胜任我想要的工作？</p><p style=\"text-align: justify;line-height: 2em;\">我适合什么类型工作？</p><p style=\"text-align: justify;line-height: 2em;\">我的竞争优势是什么？</p><p style=\"text-align: justify;line-height: 2em;\">如果求职不理想，我还有哪方面的出路？</p><p style=\"text-align: justify;line-height: 2em;\">我，不能啃老！</p></section></section></section><p><br  /></p><p style=\"text-align: center;line-height: 2em;\">正确认识自己是一切的前提</p><p style=\"text-align: center;line-height: 2em;\">你想要什么&nbsp; 你能做什么</p><p style=\"text-align: center;line-height: 2em;\">没人比你了解自己</p><p style=\"text-align: center;line-height: 2em;\">之后，行动才能检验一切</p><p style=\"text-align: center;line-height: 2em;\">光想不练可是假把式</p><p><br  /></p><p style=\"text-align: center;\">求职是一门艺术</p><section class=\"_135editor\" data-tools=\"135编辑器\" data-id=\"85638\" style=\"border-width: 0px;border-style: none;border-color: initial;box-sizing: border-box;\"><section class=\"layout\" style=\"margin-top: 2em;margin-right: auto;margin-left: auto;padding-top: 0.5em;padding-bottom: 0.5em;white-space: normal;border-right: none;border-bottom: none;border-left: none;border-top: 1px solid rgb(204, 204, 204);font-size: 1em;font-family: inherit;font-weight: inherit;text-decoration: inherit;color: rgb(166, 166, 166);width: 93%;box-sizing: border-box;\" data-width=\"93%\"><section style=\"margin-top: -1.2em;text-align: center;border-width: initial;border-style: none;border-color: initial;line-height: 1.4;box-sizing: border-box;\"><span class=\"135brush\" data-brushtype=\"text\" style=\"background-color: rgb(15, 15, 25);border-color: rgb(183, 184, 184);color: rgb(255, 255, 255);font-family: inherit;font-size: 1em;font-weight: inherit;padding: 8px 23px;text-decoration: inherit;\">避免踏进这些误区</span></section></section></section><section class=\"_135editor\" data-tools=\"135编辑器\" data-id=\"92981\" style=\"border-width: 0px;border-style: none;border-color: initial;box-sizing: border-box;\"><section data-id=\"undefined\" class=\"_135editor\" style=\"border-width: 0px;border-style: none;border-color: initial;box-sizing: border-box;\"><section style=\"width: 100%;\" data-width=\"100%\"><section style=\"padding-top: 1em;box-sizing: border-box;\"><section style=\"display: flex;justify-content: space-between;align-items:flex-end;margin-bottom: -14px;\"><section style=\"margin-left: 4em;\"><section style=\"border-width: 2px;border-style: solid;border-color: rgb(225, 19, 88);width: 12px;height: 1.6em;background: rgb(255, 255, 255);border-radius: 20px;box-sizing: border-box;\"></section></section><section style=\"margin-right: 4em;\"><section style=\"border-width: 2px;border-style: solid;border-color: rgb(225, 19, 88);width: 12px;height: 1.6em;background: rgb(255, 255, 255);border-radius: 20px;box-sizing: border-box;\"></section></section></section><section style=\"padding: 0em 10px 1em;box-sizing: border-box;\"><section style=\"border-radius: 15px;background: rgb(255, 255, 255);border-width: 2px;border-style: solid;border-color: rgb(225, 19, 88);color: rgb(225, 19, 88);box-sizing: border-box;\"><section style=\"padding: 1em 10px;box-sizing: border-box;\"><section style=\"border-radius: 15px;background: rgb(225, 19, 88);color: rgb(255, 255, 255);border-width: 2px;border-style: solid;border-color: rgb(225, 19, 88);box-sizing: border-box;\"><section class=\"135brush\" data-brushtype=\"text\" style=\"padding: 0.3em 5px;font-weight: bold;text-align: center;line-height: 1.5em;letter-spacing: 2px;box-sizing: border-box;\">过于执着专业对口</section></section><section class=\"135brush\" data-brushtype=\"text\" style=\"padding: 1em 20px;letter-spacing: 1.5px;line-height: 1.75em;font-size: 14px;color: rgb(63, 63, 62);box-sizing: border-box;\"><span style=\"font-size: 15px;\">专业对口的工作可以发挥自身技能，这再好不过。但是如果多方面试结果不理想的话，那么应当考虑第二选择，另寻其他适合自己的工作。太多的挫败只会消磨耐心，厌倦自己</span></section></section></section></section></section></section></section></section><section class=\"_135editor\" data-tools=\"135编辑器\" data-id=\"92981\" style=\"border-width: 0px;border-style: none;border-color: initial;box-sizing: border-box;\"><section data-id=\"undefined\" class=\"_135editor\" style=\"border-width: 0px;border-style: none;border-color: initial;box-sizing: border-box;\"><section style=\"width: 100%;\" data-width=\"100%\"><section style=\"padding-top: 1em;box-sizing: border-box;\"><section style=\"display: flex;justify-content: space-between;align-items:flex-end;margin-bottom: -14px;\"><section style=\"margin-left: 4em;\"><section style=\"border-width: 2px;border-style: solid;border-color: rgb(254, 125, 3);width: 12px;height: 1.6em;background: rgb(255, 255, 255);border-radius: 20px;box-sizing: border-box;\"></section></section><section style=\"margin-right: 4em;\"><section style=\"border-width: 2px;border-style: solid;border-color: rgb(254, 125, 3);width: 12px;height: 1.6em;background: rgb(255, 255, 255);border-radius: 20px;box-sizing: border-box;\"></section></section></section><section style=\"padding: 0em 10px 1em;box-sizing: border-box;\"><section style=\"border-radius: 15px;background: rgb(255, 255, 255);border-width: 2px;border-style: solid;border-color: rgb(254, 125, 3);color: rgb(254, 125, 3);box-sizing: border-box;\"><section style=\"padding: 1em 10px;box-sizing: border-box;\"><section style=\"border-radius: 15px;background: rgb(254, 125, 3);color: rgb(255, 255, 255);border-width: 2px;border-style: solid;border-color: rgb(254, 125, 3);box-sizing: border-box;\"><section class=\"135brush\" data-brushtype=\"text\" style=\"padding: 0.3em 5px;font-weight: bold;text-align: center;line-height: 1.5em;letter-spacing: 2px;box-sizing: border-box;\">过于看重体面稳定</section></section><p style=\"padding: 1em 20px;letter-spacing: 1.5px;line-height: 1.75em;font-size: 14px;color: rgb(63, 63, 62);box-sizing: border-box;\"><span style=\"font-size: 15px;\">很多人都会优先考虑工作种类，追求体面的工作，摒弃小行业职位，但没有什么是一蹴而就的。万丈高楼平地起，一开始就寻求高级职业得考虑自身是否有能力，有能力是本事，如若不能请从基层做起</span></p></section></section></section></section></section></section></section><section class=\"_135editor\" data-tools=\"135编辑器\" data-id=\"92981\" style=\"border-width: 0px;border-style: none;border-color: initial;box-sizing: border-box;\"><section data-id=\"undefined\" class=\"_135editor\" style=\"border-width: 0px;border-style: none;border-color: initial;box-sizing: border-box;\"><section style=\"width: 100%;\" data-width=\"100%\"><section style=\"padding-top: 1em;box-sizing: border-box;\"><section style=\"display: flex;justify-content: space-between;align-items:flex-end;margin-bottom: -14px;\"><section style=\"margin-left: 4em;\"><section style=\"border-width: 2px;border-style: solid;border-color: rgb(64, 82, 171);width: 12px;height: 1.6em;background: rgb(255, 255, 255);border-radius: 20px;box-sizing: border-box;\"></section></section><section style=\"margin-right: 4em;\"><section style=\"border-width: 2px;border-style: solid;border-color: rgb(64, 82, 171);width: 12px;height: 1.6em;background: rgb(255, 255, 255);border-radius: 20px;box-sizing: border-box;\"></section></section></section><section style=\"padding: 0em 10px 1em;box-sizing: border-box;\"><section style=\"border-radius: 15px;background: rgb(255, 255, 255);border-width: 2px;border-style: solid;border-color: rgb(64, 82, 171);color: rgb(64, 82, 171);box-sizing: border-box;\"><section style=\"padding: 1em 10px;box-sizing: border-box;\"><section style=\"border-radius: 15px;background: rgb(64, 82, 171);color: rgb(255, 255, 255);border-width: 2px;border-style: solid;border-color: rgb(64, 82, 171);box-sizing: border-box;\"><section class=\"135brush\" data-brushtype=\"text\" style=\"padding: 0.3em 5px;font-weight: bold;text-align: center;line-height: 1.5em;letter-spacing: 2px;box-sizing: border-box;\">盲目追求高薪</section></section><section class=\"135brush\" data-brushtype=\"text\" style=\"padding: 1em 20px;letter-spacing: 1.5px;line-height: 1.75em;font-size: 14px;color: rgb(63, 63, 62);box-sizing: border-box;\"><span style=\"font-size: 15px;\">之前听到的行业薪水那么高，怎么到我自己就低得可怜。作为一个职场新人，过于看重薪水，结果高不成低不就，白白浪费时间。社会准则一贯是工资看能力。盲目追求高薪不实际，初期积累经验才是明智之举</span></section></section></section></section></section></section></section></section><section class=\"_135editor\" data-tools=\"135编辑器\" data-id=\"92981\" style=\"border-width: 0px;border-style: none;border-color: initial;box-sizing: border-box;\"><section data-id=\"undefined\" class=\"_135editor\" style=\"border-width: 0px;border-style: none;border-color: initial;box-sizing: border-box;\"><section style=\"width: 100%;\" data-width=\"100%\"><section style=\"padding-top: 1em;box-sizing: border-box;\"><section style=\"display: flex;justify-content: space-between;align-items:flex-end;margin-bottom: -14px;\"><section style=\"margin-left: 4em;\"><section style=\"border-width: 2px;border-style: solid;border-color: rgb(61, 179, 154);width: 12px;height: 1.6em;background: rgb(255, 255, 255);border-radius: 20px;box-sizing: border-box;\"></section></section><section style=\"margin-right: 4em;\"><section style=\"border-width: 2px;border-style: solid;border-color: rgb(61, 179, 154);width: 12px;height: 1.6em;background: rgb(255, 255, 255);border-radius: 20px;box-sizing: border-box;\"></section></section></section><section style=\"padding: 0em 10px 1em;box-sizing: border-box;\"><section style=\"border-radius: 15px;background: rgb(255, 255, 255);border-width: 2px;border-style: solid;border-color: rgb(61, 179, 154);color: rgb(61, 179, 154);box-sizing: border-box;\"><section style=\"padding: 1em 10px;box-sizing: border-box;\"><section style=\"border-radius: 15px;background: rgb(61, 179, 154);color: rgb(255, 255, 255);border-width: 2px;border-style: solid;border-color: rgb(61, 179, 154);box-sizing: border-box;\"><section class=\"135brush\" data-brushtype=\"text\" style=\"padding: 0.3em 5px;font-weight: bold;text-align: center;line-height: 1.5em;letter-spacing: 2px;box-sizing: border-box;\">只追求大城市的生活</section></section><section class=\"135brush\" data-brushtype=\"text\" style=\"padding: 1em 20px;letter-spacing: 1.5px;line-height: 1.75em;font-size: 14px;color: rgb(63, 63, 62);box-sizing: border-box;\"><span style=\"font-size: 15px;\">北上广深经济发达，就业机会多，工资水平高，无数的职业人涌向大城市，但是否真的适合自己，还需考虑清楚，从性格，能力，人生规划，家庭等各方面分析，年轻人勇于拼搏是好事，但如果长期狭缝求生，那么二三线城市会是更好的选择</span></section></section></section></section></section></section></section></section><section class=\"_135editor\" data-tools=\"135编辑器\" data-id=\"92981\" style=\"border-width: 0px;border-style: none;border-color: initial;box-sizing: border-box;\"><section data-id=\"undefined\" class=\"_135editor\" style=\"border-width: 0px;border-style: none;border-color: initial;box-sizing: border-box;\"><section style=\"width: 100%;\" data-width=\"100%\"><section style=\"padding-top: 1em;box-sizing: border-box;\"><section style=\"display: flex;justify-content: space-between;align-items:flex-end;margin-bottom: -14px;\"><section style=\"margin-left: 4em;\"><section style=\"border-width: 2px;border-style: solid;border-color: rgb(197, 155, 69);width: 12px;height: 1.6em;background: rgb(255, 255, 255);border-radius: 20px;box-sizing: border-box;\"></section></section><section style=\"margin-right: 4em;\"><section style=\"border-width: 2px;border-style: solid;border-color: rgb(197, 155, 69);width: 12px;height: 1.6em;background: rgb(255, 255, 255);border-radius: 20px;box-sizing: border-box;\"></section></section></section><section style=\"padding: 0em 10px 1em;box-sizing: border-box;\"><section style=\"border-radius: 15px;background: rgb(255, 255, 255);border-width: 2px;border-style: solid;border-color: rgb(197, 155, 69);color: rgb(197, 155, 69);box-sizing: border-box;\"><section style=\"padding: 1em 10px;box-sizing: border-box;\"><section style=\"border-radius: 15px;background: rgb(197, 155, 69);color: rgb(255, 255, 255);border-width: 2px;border-style: solid;border-color: rgb(197, 155, 69);box-sizing: border-box;\"><section class=\"135brush\" data-brushtype=\"text\" style=\"padding: 0.3em 5px;font-weight: bold;text-align: center;line-height: 1.5em;letter-spacing: 2px;box-sizing: border-box;\">忽略辨别招聘信息</section></section><section class=\"135brush\" data-brushtype=\"text\" style=\"padding: 1em 20px;letter-spacing: 1.5px;line-height: 1.75em;font-size: 14px;color: rgb(63, 63, 62);box-sizing: border-box;\"><span style=\"font-size: 15px;\">多数求职者急于寻找工作，忽略对招聘信息真实性的辨别。现在很多虚假信息充斥招聘市场，轻则失财，重则失人。在求职过程中，要对企业有所了解，通过企业查询网站、网络评价等辨别企业真实性，可靠性，行程最好向亲友报备，谨慎出行</span></section></section></section></section></section></section></section></section><p style=\"text-align: center;margin-top: 10px;line-height: 1.75em;\">机会属于有准备的人</p><p style=\"text-align: center;margin-top: 10px;line-height: 1.75em;\">政府从政策、资金等大力扶持创业人才</p><p style=\"margin-top: 10px;font-size: 16px;white-space: normal;text-align: center;line-height: 1.75em;\">如果有好的策略和胆识</p><p style=\"margin-top: 10px;font-size: 16px;white-space: normal;text-align: center;line-height: 1.75em;\">可以考虑创业的道路</p><p style=\"text-align: center;line-height: 2em;margin-top: 10px;\"><br  /></p><p style=\"text-align: center;line-height: 2em;margin-top: 10px;\">如果你准备好了<br  /></p><p style=\"text-align: center;line-height: 2em;\">请带上你的自信</p><p style=\"text-align: center;line-height: 2em;\">端正态度&nbsp;&nbsp;<span style=\"caret-color: red;\">发挥优势</span></p><p style=\"text-align: center;line-height: 2em;\">机遇和挑战共存</p><p style=\"text-align: center;line-height: 2em;\">你的能力该配得上你的追求</p><p style=\"text-align: center;line-height: 2em;\">共勉！</p><p style=\"text-align: center;\"><br  /></p><p><br  /></p><section class=\"_135editor\" data-tools=\"135编辑器\" data-id=\"90242\" style=\"border-width: 0px;border-style: none;border-color: initial;box-sizing: border-box;\"><section style=\"width:100%;\" data-width=\"100%\"><section style=\"width: 260px;margin-right: auto;margin-left: auto;padding-bottom: 145px;box-sizing: border-box;\"><section style=\"line-height: 0 !important;\"><img class=\"\" data-ratio=\"1.1983333333333333\" data-src=\"https://mmbiz.qpic.cn/mmbiz_jpg/w9GMnwKCInU9PticNZeHeIb2tHC5XibpaO7qnl71J1qjpHULmdYgeSYicsGWzaN9XOHqAFLYu64HTg1zV7lvCYT7Q/640?wx_fmt=jpeg\" data-type=\"jpeg\" data-w=\"600\" data-width=\"100%\" style=\"width: 100%;\"  /></section><section style=\"width: 37%;margin-right: auto;margin-top: -238px;margin-left: 79px;line-height: 0 !important;\" data-width=\"37%\"><img class=\"\" data-copyright=\"0\" data-cropselx1=\"0\" data-cropselx2=\"96\" data-cropsely1=\"0\" data-cropsely2=\"96\" data-ratio=\"1\" data-src=\"https://mmbiz.qpic.cn/mmbiz_jpg/w9GMnwKCInXNOichjCSFf5jFWwAgJbNRZNMNtcVlCmyh1R0CFwMUwj0ibcgtgX1bArry3OmwXGvUA4tcd3MI27Iw/640?wx_fmt=jpeg\" data-type=\"jpeg\" data-w=\"860\" data-width=\"100%\" style=\"width: 96px;height: 96px;\"  /></section></section><section style=\"line-height: 1.8em;text-align: center;margin-top: 5px;\"><p>长按二维码识别关注</p></section></section></section><p><br  /></p></section><p><br  /></p>', '2018-07-09 15:46:19', '2018-07-10 17:42:12', 'JDfSfJl9ZkaORw9dFDGdcBT0pFsCfs2nvnSUrMbZgw4', '', 0, 'http://mp.weixin.qq.com/s?__biz=MzUxMzI4MTIzMA==&mid=100000065&idx=1&sn=eb66f6bfce7a11176a06031b9bf523a3&chksm=7956dce04e2155f6dac2bd2611898c5b9ea9b586b2d2cc3cba5b7425517fff4fbdd3d8f6538e#rd', 'http://mmbiz.qpic.cn/mmbiz_jpg/w9GMnwKCInU9PticNZeHeIb2tHC5XibpaOkK2c9ibFdPqYA1uZDZ9EDNMRRFFhPfNBTZma4HfREMTRkPrY8cSuXLw/0?wx_fmt=jpeg', 1, 0, '/upload/2018-07-10/2da120fd5e4e4c588de4c24ab33c2397.jpeg');
INSERT INTO `t_wechat_news` VALUES ('JDfSfJl9ZkaORw9dFDGdcD3DW7JaJjJnrGfLMCycnlU', NULL, '5G，我们期待的是什么？', '同恒恒', '大数据，万物联网，无人驾驶。。。太多太多', '<section data-role=\"outer\" label=\"Powered by 135editor.com\" style=\"font-size:16px;font-family:微软雅黑;\"><p style=\"margin-top: 5px;text-align: center;margin-left: 20px;margin-right: 20px;\"><img class=\"\" data-ratio=\"0.6791666666666667\" data-src=\"https://mmbiz.qpic.cn/mmbiz_gif/w9GMnwKCInWldTnGj4dGUUC8NCgcpl1iaH3ntiaEvasaguPVJ0HbM4BvHIrrQfSgtGhFrGO71IPkdrviaMITczGNQ/640?wx_fmt=gif\" data-type=\"gif\" data-w=\"240\" height=\"153\" style=\"width: 225px;height: 153px;\" width=\"225\"  /></p><p style=\"margin-top: 5px;text-align: center;letter-spacing: 1px;line-height: 2.5em;\"><span style=\"font-size: 15px;\">4G似乎刚上手没多久</span></p><p style=\"text-align: center;letter-spacing: 1px;line-height: 2.5em;\"><span style=\"font-size: 15px;\">5G又将来临<br  /></span></p><p style=\"line-height: 1.75em;text-align: center;margin-top: 10px;margin-bottom: 10px;\"><span style=\"font-size: 15px;\"><img class=\"\" data-ratio=\"0.5397260273972603\" data-src=\"https://mmbiz.qpic.cn/mmbiz_jpg/w9GMnwKCInWldTnGj4dGUUC8NCgcpl1iaOMRkTs8ibxMiaG68aiask6ic6FIH7FBeus5r84b2PG7xvGQpLs9LbD5TkA/640?wx_fmt=jpeg\" data-type=\"jpeg\" data-w=\"730\" height=\"202\" style=\"height: 202px;z-index: -1;cursor: pointer;white-space: normal;width: 374px;\" width=\"374\"  /></span></p><p style=\"text-align: center;line-height: 2.5em;\"><span style=\"font-size: 15px;\">从3G到4G</span></p><p style=\"text-align: center;line-height: 2.5em;\"><span style=\"font-size: 15px;\">无线流量经历了KB到MB的升级</span></p><p style=\"text-align: center;line-height: 2.5em;\"><span style=\"font-size: 15px;\">那这未来的5G呢？</span></p><p style=\"text-align: center;\"><br  /></p><p style=\"text-align: center;line-height: 2.5em;\"><span style=\"font-size: 15px;\">5G网络的理论下行速度为10Gb/s</span></p><p style=\"margin-bottom: 10px;text-align: center;line-height: 2.5em;\"><span style=\"font-size: 15px;\">（相当于下载速度1.25GB/s）</spa