INSERT INTO `t_wechat_news` VALUES ('JDfSfJl9ZkaORw9dFDGdcDEtN-DVSPE0tZBriVyOCho', NULL, '放假啦~普天同庆的国庆节', '同恒恒', '我想告诉你，不能再低调了', '<section data-role=\"outer\" label=\"Powered by 135editor.com\" style=\"font-size:16px;font-family:微软雅黑;\"><section class=\"_135editor\" data-tools=\"135编辑器\" data-id=\"93688\" style=\"border-width: 0px;border-style: none;border-color: initial;box-sizing: border-box;\"><section style=\"width:100%;display: inline-block;\" data-width=\"100%\"><section style=\"display: flex;justify-content: center;align-items: center;\"><section style=\"width:2.8em;transform: scaleX(-1);-webkit-transform: scaleX(-1);-moz-transform: scaleX(-1);-o-transform: scaleX(-1);\"><section style=\"width: 60%;height: 4px;background: rgb(212, 10, 17);border-radius: 6px;box-sizing: border-box;\" data-width=\"60%\"></section><section style=\"width: 80%;height: 4px;background: rgb(212, 10, 17);border-radius: 6px;margin-top: 5px;margin-bottom: 5px;box-sizing: border-box;\" data-width=\"80%\"></section><section style=\"width: 60%;height: 4px;background: rgb(212, 10, 17);border-radius: 6px;box-sizing: border-box;\" data-width=\"60%\"></section></section><section style=\"display:inline-block;margin-left:5px;\"><section class=\"135brush\" data-brushtype=\"text\" style=\"padding: 4px 1.5em 5px;background-image: url(&quot;https://mmbiz.qpic.cn/mmbiz_png/w9GMnwKCInXHrQLvib0RdQyFW6O9AoiaPRPawJ5fMBlaic8ND803ynD1xMVpyd5riahgLghTI0K2uvz3nWJ9rSbfjA/640?wx_fmt=png&quot;);background-repeat: no-repeat;text-align: center;background-size: 100% 100%;color: rgb(254, 255, 254);letter-spacing: 1.5px;box-sizing: border-box;\">普天同庆 爱我中华</section></section><section style=\"width:2.8em;margin-left:5px;\"><section style=\"width: 60%;height: 4px;background: rgb(212, 10, 17);border-radius: 6px;box-sizing: border-box;\" data-width=\"60%\"></section><section style=\"width: 80%;height: 4px;background: rgb(212, 10, 17);border-radius: 6px;margin-top: 5px;margin-bottom: 5px;box-sizing: border-box;\" data-width=\"80%\"></section><section style=\"width: 60%;height: 4px;background: rgb(212, 10, 17);border-radius: 6px;box-sizing: border-box;\" data-width=\"60%\"></section></section></section></section></section><p><br  /></p><p style=\"text-align: center;line-height: 2em;letter-spacing: 2px;\"><span style=\"font-size: 15px;\">明天就是国庆了</span></p><p style=\"text-align: center;line-height: 2em;letter-spacing: 2px;\"><span style=\"font-size: 15px;\">这么多年了</span></p><p style=\"text-align: center;line-height: 2em;letter-spacing: 2px;\"><span style=\"font-size: 15px;\">每到这个日子</span></p><p style=\"text-align: center;line-height: 2em;letter-spacing: 2px;\"><span style=\"font-size: 15px;\">那个隐藏在心中许久的秘密</span></p><p style=\"text-align: center;line-height: 2em;letter-spacing: 2px;\"><span style=\"font-size: 15px;\">宛如一簇烈火</span></p><p style=\"text-align: center;line-height: 2em;letter-spacing: 2px;\"><span style=\"font-size: 15px;\">点燃整个身心</span></p><p style=\"text-align: center;line-height: 2em;letter-spacing: 2px;\"><span style=\"font-size: 15px;\">&nbsp;今天，我要说出来</span></p><p style=\"text-align: center;line-height: 2em;letter-spacing: 2px;\"><span style=\"font-size: 15px;\">不能在低调了</span></p><p style=\"line-height: 2em;letter-spacing: 2px;text-align: center;margin: 15px;\"><span style=\"font-size: 15px;\"><img class=\"\" data-ratio=\"1\" data-src=\"https://mmbiz.qpic.cn/mmbiz_jpg/w9GMnwKCInXHrQLvib0RdQyFW6O9AoiaPR5OVKpVBHEwuCQXrldoRP5E8ia0WNR5sicT8BTFgMYKyk9SfVkm5zYpIA/640?wx_fmt=jpeg\" data-type=\"jpeg\" data-w=\"300\" height=\"157\" style=\"width: 157px;height: 157px;\" width=\"157\"  /></span></p><p style=\"text-align: center;line-height: 2em;letter-spacing: 2px;\"><span style=\"font-size: 15px;\">大家好</span></p><p style=\"text-align: center;line-height: 2em;letter-spacing: 2px;\"><span style=\"font-size: 15px;\">我的真实身份是祖国的花朵</span></p><p style=\"text-align: center;line-height: 2em;letter-spacing: 2px;\"><span style=\"font-size: 15px;\">优秀的社会主义接班人</span></p><p style=\"text-align: center;line-height: 2em;letter-spacing: 2px;margin-bottom: 15px;\"><span style=\"font-size: 15px;\">谢谢大家支持&nbsp;</span></p><section style=\"display: flex;justify-content: center;padding-top: 1em;padding-bottom: 1em;box-sizing: border-box;\"><section style=\"width:3em;\"><img class=\"\" data-ratio=\"1.0227272727272727\" data-src=\"https://mmbiz.qpic.cn/mmbiz_png/w9GMnwKCInXHrQLvib0RdQyFW6O9AoiaPRrLOpntB86V2uiaDxbXicQnlV52KwRfrbFp9Vr0lwdQTQvFesCJXic9vuQ/640?wx_fmt=png\" data-type=\"png\" data-w=\"132\" data-width=\"100%\" style=\"width: 100%;display: block;\"  /></section></section><p style=\"text-align: center;line-height: 2em;letter-spacing: 2px;margin-top: 15px;\"><span style=\"font-size: 15px;\">言归正传<br  /></span></p><p style=\"text-align: center;line-height: 2em;letter-spacing: 2px;\"><span style=\"font-size: 15px;\">这个普天同庆的节日里</span></p><p style=\"text-align: center;line-height: 2em;letter-spacing: 2px;\"><span style=\"font-size: 15px;\">衷心为祖国的强大而庆贺</span></p><p style=\"text-align: center;line-height: 2em;letter-spacing: 2px;\"><span style=\"font-size: 15px;\">与此同时</span></p><p style=\"text-align: center;line-height: 2em;letter-spacing: 2px;margin-bottom: 10px;\"><span style=\"font-size: 15px;\">国庆七天的假期也是十分诱人</span></p><p style=\"text-align: center;margin-top: 15px;margin-bottom: 15px;\"><img class=\"\" data-ratio=\"1\" data-src=\"https://mmbiz.qpic.cn/mmbiz_gif/w9GMnwKCInXHrQLvib0RdQyFW6O9AoiaPRIXeHiaQpJVYJljozQklvqe6giatRAZrfdkzPIGpgBr4tLPibTy0jtuxrw/640?wx_fmt=gif\" data-type=\"gif\" data-w=\"240\" height=\"146\" style=\"width: 146px;height: 146px;\" width=\"146\"  /></p><p style=\"text-align: center;line-height: 2em;letter-spacing: 2px;margin-bottom: 5px;\"><span style=\"font-size: 15px;\">这个小长假</span></p><p style=\"line-height: 2em;letter-spacing: 2px;margin-bottom: 10px;text-align: center;\"><span style=\"font-size: 15px;\">你有什么安排呢？</span></p><p><br  /></p><section class=\"_135editor\" data-tools=\"135编辑器\" data-id=\"90719\" style=\"border-width: 0px;border-s